int setup = 0;

int previousNeighbour = -1;

void AddNewNodes()
{
  int i;
  for(i=0; i<nrNewNodes; i++)
  {
    newNodes[i] = existingNodes + i;
    newNodesNeighbour[i] = -1; // initialize
  }
}

