const int N = 5; // Number of satellites

clock time;

typedef int[0, N-1] UID;

chan transmit[N];
chan acknowledge[N];
chan complete;

meta UID sender;
meta bool failure;

UID start; // The satellite that initiates

// ------  PlanGeneration  ------

typedef struct { int x; int y; int z; } pos;


int[0, 2*N] comms;

// ---- Positions -----
