const pos tp2 = {2,0,-8};
const pos tp3 = {-2,0,8};
const pos tp4 = {-2,0,-8};

const pos startPositions[N] = {sp1, sp2, sp3, sp4};
const pos targetPositions[N] = {tp1, tp2, tp3, tp4};

int chosenPos[N] = {-1,-1,-1,-1};
