const int radius = 100;
int startPositions[N];
int targetPositions[N];

int chosenPos[N];

template.declaration:
clock t;

int cost; // the cost to move to a target position
bool isTerminated;

int possiblePos[N];
int nodesBestCost[N];


UID nextHop() {
  return (ID + 1) % N;
}

// Distance between two points
/*
int Distance(pos p1, pos p2)
{
  double a = pow((p2.x - p1.x), 2);
  double b = pow((p2.y - p1.y), 2);
  double c = pow((p2.z - p1.z), 2);

  double dist = sqrt(a + b + c);
  int distInt = fint(dist);
  return  distInt;
}
*/

int minInt(int n1, int n2)
{
  if (n1 < n2)
  {
    return n1;
  }
  {
    return n2;
  }  
}

int maxInt(int n1, int n2)
{
  if (n1 > n2)
  {
    return n1;
  }
  {
    return n2;
  }
}

int Distance(int p1, int p2)
{
  int minP = minInt(p1, p2);
  int maxP = maxInt(p1, p2);

  int distance1 = maxP - minP;
  int distance2 = (radius - maxP) + minP;

  if ((distance1 < distance2) && (distance1 >= 0))
  {
    return distance1;
  }
  else
  {
    return distance2;
  }
}


// check if node with id has already chosen a position
bool isInPlan(int id)
{
  int i;
  for (i=0; i<N; i++)
  {
    if (id == chosenPos[i])
    {
      return true;
    }
  }
  return false;
}

void CalculatePositions()
{
  int i;

  // Calculate cost to all target positions
  for (i=0; i<N; i++)
  {
    nodesBestCost[i] = -1; //initalize bestNodes

    if (chosenPos[i] == -1)
    {
      possiblePos[i] = Distance(startPositions[ID], targetPositions[i]);
    }
    else
    {
      possiblePos[i] = -1;
    }
  }
}


void BestPosition(int id)
{
  int bestCost = -1;
  int bestPos;

  int i;
  for (i=0; i<N; i++)
  {
    int tempCost = Distance(startPositions[id], targetPositions[i]);

    if (tempCost < bestCost || bestCost == -1)
    {
      bestCost = tempCost;
      bestPos = i;
    }
  }

  if (bestCost < nodesBestCost[bestPos] || nodesBestCost[bestPos] == -1)
  {
    nodesBestCost[bestPos] = bestCost;
  } 

}


void ProcessPlan()
{
  int myChosenPos = -1;
  int myCost = -1;

  int i;  
  for (i=0; i<N; i++)
  {
    if (!isInPlan(i) && i != ID)
    {
      BestPosition(i);
    }
  }

  // Loop over all possible positions (smallest first)
  // Check if other nodes has each position as their best
  // Choose the first available
  for (i=0; i<N; i++)
  {
    if (possiblePos[i] != -1)
    {

      bool isBetter = possiblePos[i] <= nodesBestCost[i] || nodesBestCost[i] == -1;

      if (isBetter && (possiblePos[i] < myCost || myCost == -1))
      {
        myChosenPos = i;
        myCost = possiblePos[i];
      }
    }
  }

  chosenPos[myChosenPos] = ID;
  cost = myCost;
}

