int setup;
const int distanceBetweenNodes = radius / N;
typedef int[1, 3] OffsetPosition;

int nextPosition = 0;

// returns position close to even distribution
int EvenOffset(OffsetPosition oP)
{

  int offset = distanceBetweenNodes / 2;

  if (oP == 1) // CounterClockwise
  {
    offset -= offset / 4;
  }
  else if(oP == 2) // Middle
  {
    // do nothing 
  }
  else if(oP == 3) // Clockwise
  {
        offset += offset / 4;
  }
  return offset;
}


bool AllTargetsChosen()
{
  int i;
  for (i=0; i<N; i++)
  {
    if (chosenPos[i] == -1)
    {
      return false;
    }
  }
  return true;
}

bool Destinct(){
  bool result = true;
  int i;
  for (i=0; i<N; i++)
  {
    int count = 0;
    int j;
    for (j=0; j<N; j++)
    {
      if (chosenPos[j] == i)
      { 
        count++;
      }
    }
    if (count > 1)
    {
      result = false; 
    }
  }
  return result;
}

bool Verify() {
  return AllTargetsChosen() && Destinct();
}

